// src/components/Booking.js
import { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig";
import { collection, getDocs, addDoc } from "firebase/firestore"; // Import Firestore functions

import './Booking.scss';

const Booking = () => {
  const [rooms, setRooms] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null); // Changed initial state to null
  const [bookingDate, setBookingDate] = useState("");

  useEffect(() => {
    const fetchRooms = async () => {
      const roomsCollection = await db.collection("rooms").get();
      setRooms(roomsCollection.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };
    fetchRooms();
  }, []);

  const handleBooking = async () => {
    const user = auth.currentUser;
    if (user) {
      try {
        await db.collection("bookings").add({
          userId: user.uid,
          roomId: selectedRoom.id, // Changed to selectedRoom.id
          bookingDate,
        });
        alert("Room booked successfully!");
      } catch (error) {
        alert("Error booking room: " + error.message);
      }
    } else {
      alert("Please log in to book a room.");
    }
  };

  return (
    <div className="booking-container">
      <h2 className="booking-title">Book a Room</h2>
      <select
        onChange={(e) => {
          const room = rooms.find(room => room.id === e.target.value);
          setSelectedRoom(room); // Set selected room based on dropdown
        }}
        value={selectedRoom ? selectedRoom.id : ""} // Handle undefined state
        className="booking-select"
      >
        <option value="">Select a Room</option>
        {rooms.map(room => (
          <option key={room.id} value={room.id}>
            {room.name} - ${room.price}/night - {room.availability ? "Available" : "Unavailable"}
          </option>
        ))}
      </select>

      {/* Conditional rendering based on availability */}
      {selectedRoom && selectedRoom.availability && (
        <div>
          <input
            type="date"
            value={bookingDate}
            onChange={(e) => setBookingDate(e.target.value)}
            className="booking-date"
          />
          <button onClick={handleBooking} className="booking-button">
            Book Now
          </button>
        </div>
      )}
      {selectedRoom && !selectedRoom.availability && (
        <p className="availability-message">This room is currently unavailable.</p>
      )}
    </div>
  );
};

export default Booking;
