// src/components/Auth.js
import { useState } from "react";
import { auth } from "../firebaseConfig";
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth"; // Import them here
import './Auth.scss';

const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [login, setLogin] = useState(true);

  const handleSignUp = async () => {
    try {
      await createUserWithEmailAndPassword(auth, email, password); // Update this
      alert("Registration successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password); // Update this
      alert("Login successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="auth-container">
      <div><h1>Hotel Booking App</h1></div>
      <div className="auth-toggle">
        <a
          onClick={() => setLogin(true)}
          className={login ? "auth-title title-checked" : "auth-title"}
        >
          Login
        </a>
        <a
          onClick={() => setLogin(false)}
          className={!login ? "auth-title title-checked" : "auth-title"}
        >
          Register
        </a>
      </div>

      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="auth-input"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="auth-input"
      />

      {login ? (
        <button onClick={handleLogin} className="auth-button">
          Login
        </button>
      ) : (
        <button onClick={handleSignUp} className="auth-button">
          Register
        </button>
      )}
    </div>
  );
};

export default Auth;
