// src/components/Booking.js
import { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig";
import { collection, getDocs, addDoc } from "firebase/firestore"; // Import necessary Firestore functions
import './Booking.scss';

const Booking = () => {
    const [rooms, setRooms] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(null);
    const [bookingDate, setBookingDate] = useState("");

    useEffect(() => {
        const fetchRooms = async () => {
            try {
                const roomsCollectionRef = collection(db, "rooms");
                const roomsCollection = await getDocs(roomsCollectionRef);
                setRooms(roomsCollection.docs.map(doc => ({ id: doc.id, ...doc.data() })));
            } catch (error) {
                console.error("Error fetching rooms:", error);
            }
        };
        fetchRooms();
    }, []);

    const handleBooking = async () => {
        const user = auth.currentUser;
        if (user) {
            try {
                await addDoc(collection(db, "bookings"), {
                    userId: user.uid,
                    roomId: selectedRoom.id,
                    bookingDate,
                });
                alert("Room booked successfully!");
                // Reset selected room and date after booking
                setSelectedRoom(null);
                setBookingDate("");
            } catch (error) {
                alert("Error booking room: " + error.message);
            }
        } else {
            alert("Please log in to book a room.");
        }
    };

    return (
        <div className="booking-container">
            <h2 className="booking-title">Book a Room</h2>
            <div className="room-cards">
                {rooms.map(room => (
                    <div
                        key={room.id}
                        className={`room-card ${selectedRoom?.id === room.id ? 'selected' : ''} ${room.availability ? '' : 'unavailable'}`}
                        onClick={() => room.availability && setSelectedRoom(room)}
                    >
                        <h3 className="room-name">{room.name}</h3>
                        <p className="room-price">${room.price}/night</p>
                        <p className="room-availability">
                            {room.availability ? "Available" : "Unavailable"}
                        </p>
                    </div>
                ))}
            </div>

            {selectedRoom && selectedRoom.availability && (
                <div className="booking-form">
                    <input
                        type="date"
                        value={bookingDate}
                        onChange={(e) => setBookingDate(e.target.value)}
                        className="booking-date"
                    />
                    <button onClick={handleBooking} className="booking-button">
                        Book Now
                    </button>
                </div>
            )}
            {selectedRoom && !selectedRoom.availability && (
                <p className="availability-message">This room is currently unavailable.</p>
            )}
        </div>
    );
};

export default Booking;
