import { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig";
import { collection, getDocs, addDoc, query, where } from "firebase/firestore"; // Import query and where
import Calendar from "react-calendar"; // Import react-calendar
import 'react-calendar/dist/Calendar.css'; // Import calendar styles
import './Booking.scss';

const Booking = () => {
    const [rooms, setRooms] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(null);
    const [bookedDates, setBookedDates] = useState([]); // Store booked dates for selected room
    const [selectedDates, setSelectedDates] = useState([]); // Store selected dates

    useEffect(() => {
        const fetchRooms = async () => {
            try {
                const roomsCollectionRef = collection(db, "rooms");
                const roomsCollection = await getDocs(roomsCollectionRef);
                setRooms(roomsCollection.docs.map(doc => ({ id: doc.id, ...doc.data() })));
            } catch (error) {
                console.error("Error fetching rooms:", error);
            }
        };

        fetchRooms();
    }, []);

    useEffect(() => {
        const fetchBookedDates = async () => {
            if (!selectedRoom) return; // Do not fetch if no room is selected

            try {
                const bookingsCollectionRef = collection(db, "bookings");
                const q = query(bookingsCollectionRef, where("roomId", "==", selectedRoom.id)); // Query bookings for the selected room
                const bookingsCollection = await getDocs(q);
                const dates = bookingsCollection.docs.map(doc => doc.data().bookingDate);
                setBookedDates(dates); // Store booked dates for the selected room
            } catch (error) {
                console.error("Error fetching bookings:", error);
            }
        };

        fetchBookedDates();
    }, [selectedRoom]); // Fetch booked dates whenever the selected room changes

    const handleBooking = async () => {
        const user = auth.currentUser;
        if (user && selectedRoom && selectedDates.length > 0) {
            try {
                await Promise.all(selectedDates.map(date => {
                    // Format the date as YYYY-MM-DD
                    const formattedDate = date.toISOString().split('T')[0]; // Get the date in the format YYYY-MM-DD
                    return addDoc(collection(db, "bookings"), {
                        userId: user.uid,
                        roomId: selectedRoom.id,
                        bookingDate: formattedDate, // Store the formatted date
                    });
                }));
                alert("Room booked successfully!");
                setSelectedRoom(null);
                setSelectedDates([]);
                setBookedDates([]); // Clear booked dates
            } catch (error) {
                alert("Error booking room: " + error.message);
            }
        } else {
            alert("Please select a room and at least one date.");
        }
    };

    const isBooked = (date) => {
        return bookedDates.some(bookedDate => new Date(bookedDate).toDateString() === date.toDateString())
            ? 'booked' // Return 'booked' if the date is booked
            : null; // Return null if not booked
    };


    // Handle date selection
    const handleDateChange = (date) => {
        if (Array.isArray(date)) {
            // If it's a range selection
            const [start, end] = date;
            const datesArray = [];
            for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
                datesArray.push(new Date(d));
            }
            setSelectedDates(datesArray);
        } else {
            // If a single date is selected
            setSelectedDates([date]);
        }
    };

    const handleClear = () => {
        setSelectedDates([]);
    };

    const calculateSubtotal = () => {
        if (selectedRoom && selectedDates.length > 0) {
            return selectedRoom.price * selectedDates.length;
        }
        return 0;
    };

    const calculateTotal = () => {
        const subtotal = calculateSubtotal();
        return subtotal + subtotal * 0.15; // Adding 15% tax
    };

    return (
        <div className="booking-container">
            <h2 className="booking-title">Book a Room</h2>
            <div className="room-cards">
                {rooms.map(room => (
                    <div
                        key={room.id}
                        className={`room-card ${selectedRoom?.id === room.id ? 'selected' : ''}`}
                        onClick={() => setSelectedRoom(room)}
                    >
                        <h3 className="room-name">{room.name}</h3>
                        <p className="room-price">R{room.price}/night</p>

                    </div>
                ))}
            </div>

            {selectedRoom && (
               <>
               </>
            )}
        </div>
    );
};

export default Booking;
