// src/components/Auth.js
import { useState, useEffect } from "react";
import { auth } from "../firebaseConfig";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth"; 
import firebase from "firebase/app";
import firebaseui from "firebaseui"; // Import firebaseui
import './styling/Auth.scss';

const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [login, setLogin] = useState(true);
  const [ui, setUi] = useState(null); // State to hold the Firebase UI instance

  useEffect(() => {
    const ui = new firebaseui.auth.AuthUI(auth); // Create a new instance of Firebase UI
    setUi(ui);
  }, []);

  const handleSignUp = async () => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      alert("Registration successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert("Login successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleGoogleSignIn = () => {
    if (ui) {
      ui.start('#firebaseui-auth-container', {
        signInOptions: [
          {
            provider: firebase.auth.GoogleAuthProvider.PROVIDER_ID,
            // You can specify additional options here
          },
        ],
        // Other configuration options
        // You can customize the appearance here
        callbacks: {
          signInSuccessWithAuthResult: (authResult) => {
            alert(`Welcome ${authResult.user.displayName}!`);
            return false; // Do not redirect after sign-in
          },
        },
      });
    }
  };

  // Call handleGoogleSignIn to initialize the Firebase UI
  useEffect(() => {
    handleGoogleSignIn();
  }, [ui]);

  return (
    <div className="auth-container">
      <h1>Hotel Booking App</h1>
      <div className="auth-toggle">
        <a
          onClick={() => setLogin(true)}
          className={login ? "auth-title title-checked" : "auth-title"}
        >
          Login
        </a>
        <a
          onClick={() => setLogin(false)}
          className={!login ? "auth-title title-checked" : "auth-title"}
        >
          Register
        </a>
      </div>

      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="auth-input"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="auth-input"
      />

      {login ? (
        <button onClick={handleLogin} className="auth-button">
          Login
        </button>
      ) : (
        <button onClick={handleSignUp} className="auth-button">
          Register
        </button>
      )}

      {/* Firebase UI Container for Google Sign-In */}
      <div id="firebaseui-auth-container"></div>
    </div>
  );
};

export default Auth;
