// src/components/Auth.js
import { useState } from "react";
import { auth } from "../firebaseConfig";
import './Auth.scss';

const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [login, setLogin] = useState(true);  // True if Login is selected, false if Register is selected

  const handleSignUp = async () => {
    try {
      await auth.createUserWithEmailAndPassword(email, password);
      alert("Registration successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleLogin = async () => {
    try {
      await auth.signInWithEmailAndPassword(email, password);
      alert("Login successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="auth-container">
      {/* Toggle between Login and Register */}
      <div className="auth-toggle">
        <a 
          onClick={() => setLogin(true)} 
          className={login ? "auth-title title-checked" : "auth-title"}
        >
          Login
        </a>
        <a 
          onClick={() => setLogin(false)}  // Set to false for Register
          className={!login ? "auth-title title-checked" : "auth-title"}
        >
          Register
        </a>
      </div>

      {/* Form Input Fields */}
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="auth-input"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="auth-input"
      />

      {/* Conditionally Render Button */}
      {login ? (
        <button onClick={handleLogin} className="auth-button">
          Login
        </button>
      ) : (
        <button onClick={handleSignUp} className="auth-button">
          Register
        </button>
      )}
    </div>
  );
};

export default Auth;
