// src/components/MyBookings.js
import { useState, useEffect } from "react";
import { db, auth } from "../firebaseConfig";
import { collection, getDocs, query, where } from "firebase/firestore"; // Import Firestore functions
import './styling/MyBookings.scss'; // Create a SCSS file for styling

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      const user = auth.currentUser; // Get the current user
      if (user) {
        try {
          const bookingsCollectionRef = collection(db, "bookings");
          const q = query(bookingsCollectionRef, where("userId", "==", user.uid)); // Query for bookings of the current user
          const bookingsSnapshot = await getDocs(q);
          setBookings(bookingsSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        } catch (error) {
          console.error("Error fetching bookings:", error);
        }
      }
    };
    fetchBookings();
  }, []);

  return (
    <div className="my-bookings-container">
      <h2 className="my-bookings-title">My Bookings</h2>
      {bookings.length > 0 ? (
        <ul className="my-bookings-list">
          {bookings.map(booking => (
            <li key={booking.id} className="booking-item">
              <p>Room: {booking.name}</p>
              <p>Booking Date: {booking.bookingDate}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No bookings found.</p>
      )}
    </div>
  );
};

export default MyBookings;
